import { styled } from '@mui/material/styles'

export const StyledDiv = styled('div')`
    margin: 50px;
    display: flex;
    justify-content: center;
    
    img {
        width: 300px;
        margin-right: 50px;
    }
`;

export const StyledDescriptionDiv = styled('div')`
    background-color: #009140;
    color: white;
    padding: 12px;

    h2 {
        color: #B8860B;
    }
`;
